import styled from 'styled-components'

const Sidebar = ({className, children, navitem}) => (
  <SidebarStyles className={className}>
    <PrimaryStyles>{navitem}</PrimaryStyles>
    <SecondaryStyles>
      <h3>{navitem}</h3>
      <List>{children}</List>
    </SecondaryStyles>
  </SidebarStyles>
);

const SidebarStyles = styled('div')`
  display: grid;
  grid-template-columns: 70px auto;
  height: 100vh;
  border-right: 1px solid var(--gray200);
`;

const PrimaryStyles = styled('div')`
  background: var(--purple300);
  height: 100vh;
  grid-column: 1/2;
`;

const SecondaryStyles = styled('div')`
  background: var(--white);
  height: 100vh;
  grid-column: 2/3;
  padding: 1.5em;
`;

const List = styled('ul')`
  display: flex;
  flex-direction: column;
  margin: 0;
  padding: 0;
`;

export default Sidebar;
