import Dropdown from '../components/dropdown'
import styled from 'styled-components'

function AppSelector({className, children, title}) {
  const AppOptions = [
    { value: 'browser', label: 'browser' }, //0
    { value: 'platform', label: 'platform' }, //1
    { value: 'mobile', label: 'mobile' }, //2
  ]

  return (
    <AppSelectorStyles className={className}>
      <Dropdown defaultValue={AppOptions[0]} options={AppOptions}>
        <h3>{title}</h3>
      </Dropdown>
      <ListStyles>{children}</ListStyles>
    </AppSelectorStyles>
  )
}

const AppSelectorStyles = styled('div')`
  display: grid;
  grid-template-rows: auto 1fr;
  background: var(--gray100);
  grid-column: 1/2;
  padding: var(--space-xl) var(--space-lg);
  border-right: 1px solid var(--gray200);
  gap: 22px;
`;

const ListStyles = styled('div')`
  grid-row: 2/3;
`;

export default AppSelector;
