import FormSelect from '../components/select'

import styled from 'styled-components'
import '../styles/theme.css'

function FilterApps({ projectsOnly }) {
  const AppOptions = [
    { value: 'app1', label: 'App1' },
    { value: 'app2', label: 'App2' },
    { value: 'app3', label: 'App3' },
  ]

  const ProjectOptions = [
    { value: '[Selected]', label: '[Selected]' },
  ]

  const EnvOptions = [
    { value: 'all', label: 'All Env' },
    { value: 'prod', label: 'prod' },
    { value: 'stag', label: 'stag' },
  ]

  const DateOptions = [
    { value: '24h', label: '24h' },
    { value: '14d', label: '14d' },
  ]

  const AppFilterOptions = <AppFormSelect defaultValue={AppOptions[0]} options={AppOptions} />
  const ProjectFilterOptions = <AppFormSelect defaultValue={ProjectOptions[0]} options={ProjectOptions} />

  return (
    <FilterAppsStyles>
      { projectsOnly ? ProjectFilterOptions : AppFilterOptions }
      <EnvFormSelect 
        defaultValue={EnvOptions[0]}
        options={EnvOptions}
      />
      <DateFormSelect 
        defaultValue={DateOptions[0]}
        options={DateOptions}
      />
    </FilterAppsStyles>
  )
}

const FilterAppsStyles = styled('div')`
  display: flex;
`;

const AppFormSelect = styled(FormSelect)`
  .react-select__control {
    border-radius: var(--radius) 0 0 var(--radius);
  }
`;

const EnvFormSelect = styled(FormSelect)`
  .react-select__control {
    border-radius: 0;
    border-left: 0;
    border-right: 0;
  }
`;

const DateFormSelect = styled(FormSelect)`
  .react-select__control {
    border-radius: 0 var(--radius) var(--radius) 0;
  }
`;

export default FilterApps;
