import styled from 'styled-components'
import '../styles/theme.css'

const NavItem = ({className, children}) => (
    <NavItemStyles className={className}>
      {children}
    </NavItemStyles>
);

const NavItemStyles = styled('div')`	
	a {
		display: block;
		border-radius: 5px;
		cursor: pointer;
		font-weight: 500;
		font-size: 0.9em;
		color: var(--white);
		border: 1px solid transparent;
		border-bottom-width: 3px;
		background: transparent;
		color: var(--purple);
		text-decoration: none;
		text-align: center;
		padding: 12px;
	}

	.active {
		border-color: var(--black);
		background: var(--purple200);
		color: var(--white);
	}
`;

export default NavItem;
