import styled from 'styled-components'
import Logo from '../components/logo'
import '../styles/theme.css'

const SubnavItem = ({className, children, src}) => (
    <SubnavItemStyles className={className}>
      {src ? <StyledLogo src={src}/> : null}
      <span>{children}</span>
    </SubnavItemStyles>
);

const SubnavItemStyles = styled('div')`
  display: flex;
  align-items: center;
  margin-bottom: var(--space-unit);

  a {
    text-decoration: none;
    color: var(--gray200);
  }

  a.active {
    color: var(--black);

    img { 
      opacity: 100%;
    }
  }
`;

const StyledLogo = styled(Logo)`
  margin-right: var(--space-sm);
  width: var(--space-lg);
  height: var(--space-lg);
`

export default SubnavItem;
