import FormSelect from '../components/select'
import styled from 'styled-components'

function Subnavigation({className, children, title, hasAppSelector}) {
  const AppOptions = [
    { value: 'app1', label: 'App1' }, //0
    { value: 'app2', label: 'App2' }, //1
    { value: 'app3', label: 'App3' }, //2
  ]

  const AppSelector = <FormSelect defaultValue={AppOptions[0]} options={AppOptions} unstyled />;

  return (
    <SubnavigationStyles className={className}>
      <HeaderStyles>
        <TitleStyles>{title}</TitleStyles>
        {hasAppSelector ? AppSelector : null}
      </HeaderStyles>
      <ListStyles>{children}</ListStyles>
    </SubnavigationStyles>
  )
}

const SubnavigationStyles = styled('div')`
  display: grid;
  grid-template-rows: auto 1fr;
  background: var(--gray100);
  grid-column: 1/2;
  padding: var(--space-lg);
  border-right: 1px solid var(--gray200);
`;

const HeaderStyles = styled('div')`
  grid-row: 1/2;
  display: flex;
  flex-direction: column;

`;

const TitleStyles = styled('h3')`
  margin-bottom: var(--space-sm);
`;

const ListStyles = styled('div')`
  grid-row: 2/3;
`;

export default Subnavigation;
