import styled from 'styled-components'
import '../styles/theme.css'

const NavItem = ({className, children}) => (
    <NavItemStyles className={className}>
        {children}
    </NavItemStyles>
);

const NavItemStyles = styled('li')`
	border-radius: 5px;
	cursor: pointer;
	font-weight: 500;
	font-size: 0.9em;
	color: var(--white);
	border: 1px solid var(--black);
	border-bottom-width: 3px;
	background: var(--purple200);

	a {
		color: var(--white);
		text-decoration: none;
	}
`;

export default NavItem;
